mapper.select:
SELECT *
from house h,houseinformation hi,facility f where h.house_id = hi.house_id and h.house_id=f.house_id


        h.house_id=#{solr}

SELECT *
from house h,houseinformation hi,facility f where h.house_id = hi.house_id and h.house_id=f.house_id


        h.house_id=#{solr}



    and hi.province=#{houseInformation.province}


    and hi.city=#{houseInformation.city}


    and hi.rent_type=#{houseInformation.rentType}


    and h.live_nubmer <=#{house.liveNubmer}


    and h.house_price >=#{minPrice}


    and h.house_price <=#{maxPrice}


    and hi.house_information_address=#{houseInformation.houseInformationAddress}



        #{huxing}




        f.facility_one=#{sheshi} or f.facility_two=#{sheshi} or f.facility_three=#{sheshi} or
        f.facility_four=#{sheshi} or f.facility_five=#{sheshi} or f.facility_six=#{sheshi} or
        f.facility_orther=#{sheshi}



    ORDER BY house_price

select c.house_id,count(*) as houseCommentCount
from house h,houseinformation hi,facility f,comment c where h.house_id = hi.house_id and h.house_id=f.house_id
and f.house_id=c.house_id

    and hi.province=#{houseInformation.province}


    and hi.city=#{houseInformation.city}


    and hi.rent_type=#{houseInformation.rentType}


    and h.live_nubmer <=#{house.liveNubmer}


    and h.house_price >=#{minPrice}


    and h.house_price <=#{maxPrice}


    and hi.house_information_address=#{houseInformation.houseInformationAddress}



        #{huxing}




        f.facility_one=#{sheshi} or f.facility_two=#{sheshi} or f.facility_three=#{sheshi} or
        f.facility_four=#{sheshi} or f.facility_five=#{sheshi} or f.facility_six=#{sheshi} or
        f.facility_orther=#{sheshi}


group by h.house_id

    ORDER BY house_price
